mapper.select:
SELECT c.`object_id` FROM `${entity}_correction` c WHERE c.`organization_id` = #{organizationId} AND c.`correction` = #{correction}
AND EXISTS(SELECT 1 FROM `${entity}` e WHERE e.`object_id` = c.`object_id` AND e.`status` = 'ACTIVE' AND e.`parent_id` = #{internalParentId})
LIMIT 0,1

SELECT c.`object_id` FROM `building_correction` c WHERE c.`organization_id` = #{organizationId} AND
c.`correction` = #{correction}

    
        AND c.`correction_corp` IS NULL
    
    
        AND c.`correction_corp` = #{correctionCorp}
    


    AND EXISTS(SELECT 1 FROM `building` e WHERE e.`object_id` = c.`object_id` AND e.`status` = 'ACTIVE' AND e.`parent_id` = #{internalParentId})

LIMIT 0,1

SELECT c.`correction`, c.`organization_code` FROM `${entity}_correction` c
WHERE c.`organization_id` = #{organizationId}
AND c.`object_id` = #{internalObjectId}
LIMIT 0,1

SELECT c.`correction`, c.`correction_corp`, c.`organization_code` FROM `building_correction` c
WHERE c.`organization_id` = #{organizationId}
AND c.`object_id` = #{internalObjectId}
LIMIT 0,1

SELECT etc.`type` type, etc.`organization_type_code` code
FROM `entity_type_correction` etc
WHERE etc.`organization_id` = #{organizationId}
AND etc.`entity_type_id` = #{internalEntityTypeId}
LIMIT 0,1

SELECT e.`object_id` FROM `${entity}` e WHERE e.`status` = 'ACTIVE'

    AND e.`parent_id` = #{parentId}


   AND e.`entity_type_id` = #{entityTypeId}

AND EXISTS(
SELECT 1 FROM `${entity}_attribute` attr WHERE attr.`status` = 'ACTIVE' AND e.`object_id` = attr.`object_id`
AND attr.`attribute_type_id` = #{attributeTypeId}
AND attr.`value_id` IN (
        SELECT sc.`id` FROM `${entity}_string_culture` sc WHERE TO_CYRILLIC(sc.`value`) = TO_CYRILLIC(#{correction})))

SELECT b.`object_id` FROM `building` b WHERE b.`status` = 'ACTIVE'
AND e.`parent_id` = #{parentId}
AND EXISTS( SELECT 1 FROM `building_attribute` a_num JOIN `building_attribute` a_street ON ((a_num.`attribute_id` = a_street.`attribute_id`) AND (a_num.`object_id` = a_street.`object_id`) AND (a_street.`attribute_type_id` = 503) AND (a_street.`status` = 'ACTIVE')) LEFT JOIN `building_attribute` a_corp ON ((a_num.`attribute_id` = a_corp.`attribute_id`) AND (a_num.`object_id` = a_corp.`object_id`) AND (a_corp.`attribute_type_id` = 501) AND (a_corp.`status` = 'ACTIVE')) WHERE a_num.`attribute_type_id` = 500 AND a_num.`status` = 'ACTIVE' AND b.`object_id` = a_num.`object_id` AND a_num.`value_id` IN ( SELECT sc.`id` FROM `building_string_culture` sc WHERE TO_CYRILLIC(sc.`value`) = TO_CYRILLIC(#{number})) AND a_street.`value_id` = #{streetId} AND a_corp.`value_id` IN ( SELECT sc.`id` FROM `building_string_culture` sc WHERE
TO_CYRILLIC(sc.`value`) = TO_CYRILLIC(#{corp})
sc.`value` IS NULL
))

SELECT c.`correction`, c.`organization_code`, c.`organization_id`, (SELECT osc.`value` FROM `organization_string_culture` osc WHERE osc.`locale` = #{locale} AND osc.`id` IN (SELECT oa.`value_id` FROM `organization_attribute` oa WHERE oa.`status` = 'ACTIVE' AND oa.`object_id` = c.`organization_id` AND oa.`attribute_type_id` = 900)) organization, c.`object_id` internalObjectId FROM `${entity}_correction` c
ORDER BY ${orderByClause}
ASC
DESC
LIMIT #{start},#{size}
