mapper.select:
SELECT DISTINCT r.`id` id, r.`request_file_id` requestFileId, r.`F_NAM` fNam, r.`M_NAM` mNam, r.`SUR_NAM` surNam, IFNULL(p.`status`, 'ADDRESS_UNRESOLVED') as status, (SELECT sc.`value` FROM `city_string_culture` sc WHERE sc.`locale` = #{locale} AND sc.`id` = (SELECT a.`value_id` FROM `city_attribute` a WHERE a.`attribute_type_id` = 400 AND a.`status` = 'ACTIVE' AND a.`object_id` = p.`city_id`)) internalCity, (SELECT sc.`value` FROM `street_string_culture` sc WHERE sc.`locale` = #{locale} AND sc.`id` = (SELECT a.`value_id` FROM `street_attribute` a WHERE a.`attribute_type_id` = 300 AND a.`status` = 'ACTIVE' AND a.`object_id` = p.`street_id`)) internalStreet, (SELECT sc.`value` FROM `building_string_culture` sc WHERE sc.`locale` = #{locale} AND sc.`id` = (SELECT a1.`value_id` FROM `building_attribute` a1 JOIN `building_attribute` a2 ON (a1.`attribute_id` = a2.`attribute_id` AND a1.`object_id` = a2.`object_id`) WHERE a1.`attribute_type_id` = 500 AND a1.`status` = 'ACTIVE' AND a2.`attribute_type_id` = 503 AND a2.`status` = 'ACTIVE' AND a2.`value_id` = p.`street_id` AND a1.`object_id` = p.`building_id` AND a2.`object_id` = p.`building_id` ) ) internalBuilding, (SELECT sc.`value` FROM `apartment_string_culture` sc WHERE sc.`locale` = #{locale} AND sc.`id` = (SELECT a.`value_id` FROM `apartment_attribute` a WHERE a.`attribute_type_id` = 100 AND a.`status` = 'ACTIVE' AND a.`object_id` = p.`apartment_id`)) internalApartment FROM `request_benefit` r JOIN `request_file` rf ON r.`request_file_id` = rf.`id` LEFT JOIN `request_payment` p ON r.`OWN_NUM_SR` = p.`OWN_NUM_SR` LEFT JOIN `request_file` pf ON p.`request_file_id` = pf.`id` WHERE (pf.`organization_object_id` IS NULL OR rf.`organization_object_id` = pf.`organization_object_id`) AND (pf.`date` IS NULL OR EXTRACT(YEAR_MONTH FROM rf.`date`) = EXTRACT(YEAR_MONTH FROM pf.`date`)) AND r.`request_file_id` = #{requestFileId}
ORDER BY ${orderByClause}
ASC
DESC
limit #{start},#{size}
