mapper.sql:
        
            (p.`id` is not null or b.`id` is not null) 
            and g.`id` = #{id}
            and YEAR(p.`loaded`) = YEAR(#{loaded}) and DAY(p.`loaded`) = DAY(#{loaded})
            and p.`directory` like CONCAT('%',#{directory},'%')
            and p.`name` like CONCAT('%',#{paymentName},'%')
            and b.`name` like CONCAT('%',#{benefitName},'%')            
            and p.`organization_id` = #{organization.id}
            and p.`registry` = #{registry}
            and p.`year` = #{year}
            and p.`month` = #{month}
            and p.`dbf_record_count` like CONCAT('%',#{dbfRecordCount},'%')
            and g.`status` = #{status}
        

        GROUP BY g.`id`

        
            and loaded_record_count like CONCAT('%',#{loadedRecordCount},'%')
            and binded_record_count like CONCAT('%',#{bindedRecordCount},'%')
            and filled_record_count like CONCAT('%',#{filledRecordCount},'%')
