mapper.select:
SELECT c.`id`, c.`correction`, c.`organization_code`, c.`organization_id`, IFNULL((SELECT osc.`value` FROM `organization_string_culture` osc WHERE osc.`locale` = #{locale} AND osc.`id` IN (SELECT oa.`value_id` FROM `organization_attribute` oa WHERE oa.`status` = 'ACTIVE' AND oa.`object_id` = c.`organization_id` AND oa.`attribute_type_id` = 900)), (SELECT osc.`value` FROM `organization_string_culture` osc WHERE osc.`locale` = (SELECT l.`locale` FROM `locales` l WHERE l.`system` = 1) AND osc.`id` IN (SELECT oa.`value_id` FROM `organization_attribute` oa WHERE oa.`status` = 'ACTIVE' AND oa.`object_id` = c.`organization_id` AND oa.`attribute_type_id` = 900)) ) organization, c.`object_id` internalObjectId FROM `${entity}_correction` c
ORDER BY ${orderByClause}
ASC
DESC
LIMIT #{start},#{size}

SELECT etc.`id`, etc.`type` correction, etc.`organization_type_code` organization_code, etc.`organization_id`, IFNULL((SELECT osc.`value` FROM `organization_string_culture` osc WHERE osc.`locale` = #{locale} AND osc.`id` IN (SELECT oa.`value_id` FROM `organization_attribute` oa WHERE oa.`status` = 'ACTIVE' AND oa.`object_id` = etc.`organization_id` AND oa.`attribute_type_id` = 900)), (SELECT osc.`value` FROM `organization_string_culture` osc WHERE osc.`locale` = (SELECT l.`locale` FROM `locales` l WHERE l.`system` = 1) AND osc.`id` IN (SELECT oa.`value_id` FROM `organization_attribute` oa WHERE oa.`status` = 'ACTIVE' AND oa.`object_id` = etc.`organization_id` AND oa.`attribute_type_id` = 900)) ) organization, etc.`entity_type_id` internalObjectId, IFNULL((SELECT sc.`value` FROM `string_culture` sc WHERE sc.`locale` = #{locale} AND sc.`id` IN (SELECT et.`entity_type_name_id` FROM `entity_type` et WHERE et.`id` = etc.`entity_type_id`)), (SELECT sc.`value` FROM `string_culture` sc WHERE sc.`locale` = (SELECT l.`locale` FROM `locales` l WHERE l.`system` = 1) AND sc.`id` IN (SELECT et.`entity_type_name_id` FROM `entity_type` et WHERE et.`id` = etc.`entity_type_id`))) internalObject FROM `entity_type_correction` etc
ORDER BY ${orderByClause}
ASC
DESC
LIMIT #{start},#{size}
