mapper.select:
SELECT c.`${entity}` correction, c.`organization_${entity}_code` code FROM `${entity}_correction` c
WHERE c.`organization_id` = #{organizationId}
AND c.`${entity}_id` = #{internalObjectId}
LIMIT 0,1

SELECT c.`building_num`, c.`building_corp`, c.`organization_building_code` code FROM `building_correction` c
WHERE c.`organization_id` = #{organizationId}
AND c.`building_id` = #{internalObjectId}
LIMIT 0,1

SELECT etc.`type` type, etc.`organization_type_code` code
FROM `entity_type_correction` etc
WHERE etc.`organization_id` = #{organizationId}
AND etc.`entity_type_id` = #{internalEntityTypeId}
LIMIT 0,1

SELECT c.`${entity}` correction, c.`organization_${entity}_code` code, c.`organization_id`, (SELECT osc.`value` FROM `organization_string_culture` osc WHERE osc.`locale` = #{locale} AND osc.`id` IN (SELECT oa.`value_id` FROM `organization_attribute` oa WHERE oa.`status` = 'ACTIVE' AND oa.`object_id` = c.`organization_id` AND oa.`attribute_type_id` = 900)) organization, c.`${entity}_id` internalObjectId FROM `${entity}_correction` c
ORDER BY ${orderByClause}
ASC
DESC
LIMIT #{start},#{size}
