mapper.select:
SELECT * FROM `${entityTable}_correction`
`organization_id` = #{organizationId} AND TRIM(`correction`) = TRIM(#{correction}) AND `parent_id` = #{parentId}
LIMIT 1

SELECT c.`correction`, c.`organization_code` FROM `${entity}_correction` c
WHERE c.`organization_id` = #{organizationId}
AND c.`object_id` = #{objectId}

SELECT c.`correction`, c.`correction_corp`, c.`organization_code` FROM `building_correction` c
WHERE c.`organization_id` = #{organizationId}
AND c.`object_id` = #{objectId}

SELECT DISTINCT e.`object_id` FROM `${entity}` e WHERE e.`status` = 'ACTIVE'
AND e.`parent_id` = #{parentId}
AND EXISTS( SELECT 1 FROM `${entity}_attribute` attr WHERE attr.`status` = 'ACTIVE' AND e.`object_id` = attr.`object_id` AND attr.`attribute_type_id` = #{attributeTypeId} AND attr.`value_id` IN ( SELECT sc.`id` FROM `${entity}_string_culture` sc WHERE sc.`value` = TRIM(TO_CYRILLIC(#{correction}))))

SELECT c.*, (SELECT osc.`value` FROM `organization_string_culture` osc WHERE osc.`locale` = #{locale} AND osc.`id` IN (SELECT oa.`value_id` FROM `organization_attribute` oa WHERE oa.`status` = 'ACTIVE' AND oa.`object_id` = c.`organization_id` AND oa.`attribute_type_id` = 900) ) organization, IFNULL((SELECT osc.`value` FROM `organization_string_culture` osc WHERE osc.`locale` = #{locale} AND osc.`id` IN (SELECT oa.`value_id` FROM `organization_attribute` oa WHERE oa.`status` = 'ACTIVE' AND oa.`object_id` = c.`internal_organization_id` AND oa.`attribute_type_id` = 900)), (SELECT osc.`value` FROM `organization_string_culture` osc WHERE osc.`locale` = (SELECT l.`locale` FROM `locales` l WHERE l.`system` = 1) AND osc.`id` IN (SELECT oa.`value_id` FROM `organization_attribute` oa WHERE oa.`status` = 'ACTIVE' AND oa.`object_id` = c.`internal_organization_id` AND oa.`attribute_type_id` = 900)) ) internalOrganization FROM `building_correction` c
ORDER BY ${orderByClause}
ASC
DESC
LIMIT #{start},#{size}
