mapper.select:
SELECT r.`status`, r.`file_id` fileId, r.`f_nam` fNam, r.`m_nam` mNam, r.`sur_nam` surNam, (SELECT f.`name` FROM `file` f WHERE f.`id` = r.`file_id`) fileName, (SELECT sc.`value` FROM `city_string_culture` sc WHERE sc.`locale` = #{locale} AND sc.`id` = (SELECT a.`value_id` FROM `city_attribute` a WHERE a.`attribute_type_id` = 400 AND a.`status` = 'ACTIVE' AND a.`object_id` = r.`city_id`)) internalCity, (SELECT sc.`value` FROM `street_string_culture` sc WHERE sc.`locale` = #{locale} AND sc.`id` = (SELECT a.`value_id` FROM `street_attribute` a WHERE a.`attribute_type_id` = 300 AND a.`status` = 'ACTIVE' AND a.`object_id` = r.`street_id`)) internalStreet, (SELECT sc.`value` FROM `building_string_culture` sc WHERE sc.`locale` = #{locale} AND sc.`id` = (SELECT a1.`value_id` FROM `building_attribute` a1 JOIN `building_attribute` a2 ON (a1.`attribute_id` = a2.`attribute_id` AND a1.`object_id` = a2.`object_id`) WHERE a1.`attribute_type_id` = 500 AND a1.`status` = 'ACTIVE' AND a1.`object_id` = r.`building_id` AND a2.`attribute_type_id` = 503 AND a2.`status` = 'ACTIVE' AND a2.`object_id` = r.`building_id` AND a2.`value_id` = r.`street_id` ) ) internalBuilding, (SELECT sc.`value` FROM `apartment_string_culture` sc WHERE sc.`locale` = #{locale} AND sc.`id` = (SELECT a.`value_id` FROM `apartment_attribute` a WHERE a.`attribute_type_id` = 100 AND a.`status` = 'ACTIVE' AND a.`object_id` = r.`apartment_id`)) internalApartment FROM `request_payment` r
ORDER BY #{orderByClause}
ASC
DESC
limit #{start},#{size}
