SELECT DISTINCT o.`object_id` FROM `organization` o JOIN `organization_attribute` a ON (o.`object_id` = a.`object_id` AND a.`status` = 'ACTIVE') JOIN `organization_string_culture` sc ON (a.`value_id` = sc.`id`) WHERE (o.`status` IN ('ACTIVE', 'INACTIVE')) AND sc.`locale` = (SELECT l.`locale` FROM `locales` l WHERE l.`system` = 1) AND a.`attribute_type_id` = 901 AND sc.`value` = #{code}
AND o.`parent_entity_id` = #{parentEntityId} AND o.`parent_id` = #{parentId}
AND o.`parent_entity_id` IS NULL AND o.`parent_id` IS NULL

